function debounce(timescope) {
  return function (fn) {
    let timeout = null;

    return function () {
      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        fn.apply(this, arguments);
      }, timescope);
    };
  };
}

2. èŠ‚æµ
function throttle(timescope) {
  return function (fn) {
    let cooldown = false;

    return function () {
      if (cooldown) {
        return;
      }

      cooldown = true;
      fn.apply(this.arguments);

      setTimeout(() => {
        cooldown = false;
      }, timescope);
    };
  };
}

